'use strict'
/* eslint-disable no-unused-vars */
import { Request } from '../request'
import queryString from 'query-string'
import _ from 'lodash'

const model = 'apparience'

export class Apparience extends Request {
  constructor (params) {
    params.model = model
    super(params)
  }
  /**
  * POST /v1/apparience/generateWallpaper [PRIVATE]
  *
  * Generate wallpaper
  *
  * @param  {Object} params
  * - @param  {String} cell_size * -> (In Body)  e.g 200
  * - @param  {String} seed * -> (In Body)  e.g 0
  * - @param  {String} stroke_width * -> (In Body)  e.g 1.51
  * - @param  {String} variance * -> (In Body)  e.g 0.75
  * - @param  {String} x_colors * -> (In Body)  e.g #1A1C24,#72D8AB,#F3D483,#DA9844,#D6402F
  * - @param  {String} blur  -> (In Body)  e.g 170
  * @param  {Function} callback
  * @return {Code} 200, 401
  */
  generateWallpaper (body, callback) {
    let params = {url: '/v1/apparience/generateWallpaper', body}
    this.post(params, (err, data) => {
      if (err) return callback(err)
      callback(null, data)
    })
  }
  /**
  * GET /v1/apparience/getColorsFromImage [PRIVATE]
  *
  * Return an array of color taken from an online image
  *
  * @param  {Object} params
  * - @param  {String} url  -> (In query) url of image to extract colors
  * - @param  {String} colorsNumber  -> (In query) The number of colors to generate
  * - @param  {String} sortBy  -> (In query) Sort colors by...
  * @param  {Function} callback
  * @return {Code} 200, 403
  */
  getColorsFromImage (params, callback) {
    let url = '/v1/apparience/getColorsFromImage'
    if (!_.isEmpty(params)) url += '?' + queryString.stringify(params)
    this.get(url, (err, data) => {
      if (err) return callback(err)
      callback(null, data)
    })
  }
  /**
  * GET /v1/apparience/randomPalettes [PRIVATE]
  *
  * Return an array with ramdom color palettes in HEX format
  *
  * @param  {Object} params
  * - @param  {String} count  -> (In query) Number of color palettes to return
  * - @param  {String} swatchesNum  -> (In query) Number of swatches that palette should have (4 or 5 as option)
  * @param  {Function} callback
  * @return {Code} 200, 400
  */
  randomPalettes (params, callback) {
    let url = '/v1/apparience/randomPalettes'
    if (!_.isEmpty(params)) url += '?' + queryString.stringify(params)
    this.get(url, (err, data) => {
      if (err) return callback(err)
      callback(null, data)
    })
  }
  /**
  * GET /v1/apparience/topPalettes [PRIVATE]
  *
  * Return an array with the best color palettes in HEX format
  *
  * @param  {Object} params
  * - @param  {String} count  -> (In query) Number of color palettes to return
  * @param  {Function} callback
  * @return {Code} 200, 400
  */
  topPalettes (params, callback) {
    let url = '/v1/apparience/topPalettes'
    if (!_.isEmpty(params)) url += '?' + queryString.stringify(params)
    this.get(url, (err, data) => {
      if (err) return callback(err)
      callback(null, data)
    })
  }
  /**
  * POST /v1/apparience/uploadLogo [PRIVATE]
  *
  * Upload apparience photo
  *
  * @param  {Object} params
  * - @param  {String} base64 * -> (In Body)  e.g data:image/png;base64,
  * @param  {Function} callback
  * @return {Code} 200, 400, 403
  */
  uploadLogo (body, callback) {
    let params = {url: '/v1/apparience/uploadLogo', body}
    this.post(params, (err, data) => {
      if (err) return callback(err)
      callback(null, data)
    })
  }
  /**
  * POST /v1/apparience/uploadWallpaper [PRIVATE]
  *
  * Upload wallpaper
  *
  * @param  {Object} params
  * - @param  {String} wallpaper * -> (In Body)  e.g data:image/png;base64,
  * @param  {Function} callback
  * @return {Code} 200, 401
  */
  uploadWallpaper (body, callback) {
    let params = {url: '/v1/apparience/uploadWallpaper', body}
    this.post(params, (err, data) => {
      if (err) return callback(err)
      callback(null, data)
    })
  }
}
